import React from 'react'
import axios from 'axios'
import { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import img from "../image/cardLogo.png"
import { useSelector } from "react-redux"
import ReactPaginate from 'react-paginate';



function Card() {
  const [card, setCard] = useState([])
  const [isLoading, setIsLoading] = useState(false)
  // const [currentPage, setCurrentPage] = useState(1);
  // const [postPerPage, setPostPerPage] = useState(6);


  // const lastPostIndex = currentPage * postPerPage;
  // const firstPostIndex = lastPostIndex - postPerPage;
  // const currentPost = card.slice(firstPostIndex, lastPostIndex)
  // const totalPosts = card.length

  const selector = useSelector(state => state.searchDatareducer)


  // let pages = [];

  // for (let i = 1; i <= Math.ceil(totalPosts / postPerPage); i++) {
  //   pages.push(i)
  // }



  useEffect(() => {
    setIsLoading(true)
    // setTimeout(() => {
    axios.get(`https://fakestoreapi.com/products`)
      .then(res => res.data)
      .then(res => {
        setIsLoading(false)
        setCard(res)
      })
    // }, 1000);
  }, [])



  const [currentItems, setCurrentItems] = useState([]);
  const [pageCount, setPageCount] = useState(0);
  const [itemOffset, setItemOffset] = useState(0);
  const itemsPerPage=6

  useEffect(() => {
    const endOffset = itemOffset + itemsPerPage;
    setCurrentItems(card.slice(itemOffset, endOffset));
    setPageCount(Math.ceil(card.length / itemsPerPage));
  }, [itemOffset, itemsPerPage,card]);


  const handlePageClick = (event) => {
    const newOffset = (event.selected * itemsPerPage) % card.length;
    setItemOffset(newOffset);
  };




  return (
    <div>
      {isLoading && <div className="container-load">
        <div className="flex">
          <div className="loader">
          </div>
        </div>
        <div className="load-text">
          Loading...
        </div>
      </div>
      }
      <div className='container'>
        <div className='row'>
          {
            currentItems.filter((a) => {
              if (selector == "") {
                return a
              }
              else if (a.title.toLowerCase().includes(selector.toLowerCase())) {
                return a
              }
            }).map((a, b) => (
              <div className="col-lg-4 col-md-6 col-sm-12 d-flex justify-content-center" key={b}>
                <div className='card' >
                  <img src={img} className="card-img-top" alt="..." />
                  <div className="card-body">
                    <h5 className="card-title">{a.title}</h5>
                    <Link to="projectDetail" className='more-detail'><button className="btn">More Detail</button></Link>
                  </div>
                </div>
              </div>
            ))
          }
          {/* <div className='pagination'>
            {
              pages.map((a, b) => (
                <button key={b} onClick={() => setCurrentPage(a)} className={a == currentPage ? "active" : ''}>
                  {a}
                </button>
              ))
            }
          </div> */}

          <div>
            <ReactPaginate
              breakLabel="..."
              nextLabel="next >"
              onPageChange={handlePageClick}
              pageRangeDisplayed={5}
              pageCount={pageCount}
              previousLabel="< previous"
              renderOnZeroPageCount={null}
            />
          </div>

        </div>
      </div>
    </div>
  )
}

export default Card
