        setData(data.filter((a) => a.id !== e));
    };

    const handleModal=()=>{

    }
    const handleInput=(e)=>{
        setInputValue({ ...inputValue, [e.target.name]: e.target.value })
    }
    const handleAdd=(e)=>{
        e.preventDefault()
    setData([...data, inputValue])

    }

    const [modalOpen, setModalOpen] = useState(false);


    return (
        <>
        <div className='dataTable'>
            <div className="datatableTitle">
                Add New User
                <span className="link" onClick={()=>setModalOpen(true)}>
                    Add New
                </span>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Project logo</th>
                        <th>Project Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data.map((a, b) => (
                            <tr key={b}>
                                <td>{a.id}</td>
                                <td><img className='cellImg' src={a.image} /></td>
                                <td>{a.projectName}</td>
                                <td><button className='editButton'>Edit</button> <button className='deleteButton' onClick={() => handleDelete(a.id)}>Delete</button></td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>

        </div>
        {/* <div className={modal ? 'popUp activePopUp' : "popUP" }>
        <div className="table-modal">
           <form>
           <label>Project Name</label>
           <input type="text" placeholder='Write the project Name' name="projectName" onChange={(e)=>handleInput(e)} />
           <label>Project Logo</label>
           <input type="url" placeholder='Paste the image url' name="image" onChange={(e)=>handleInput(e)}/>
           <button onClick={(e)=>handleAdd(e)}>Add</button>
           </form>
           <span onClick={()=>setModal(modal=>!modal)}>x</span>
        </div>
    </div> */}

